
public class Principal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i= 1;
		int suma = 0;
		
		while(i <= 25) {
			if ((i%2) == 0);{
				suma = suma + i; 
			}
		i++; 
		}
		System.out.println("La suma de los numeros pares del 1 al 25 es: " + suma);


	}

}
